!function(){let e=document.querySelector(".header__burger"),a=document.querySelector(".header__nav-cross"),r=document.querySelector(".header__nav"),c=document.querySelector(".header__nav-wrapper");e.addEventListener("click",()=>{r.classList.add("header__nav_active"),c.classList.add("header__nav-wrapper_active")}),a.addEventListener("click",()=>{r.classList.remove("header__nav_active"),c.classList.remove("header__nav-wrapper_active")})}();